mapper.delete:
DELETE FROM
	monthly_transaction
WHERE
	monthly_transaction_id = #{monthlyTransactionId}
AND
	user_no = #{userNo};

mapper.insert:
INSERT INTO monthly_transaction( user_no, monthly_transaction_name, monthly_transaction_amount, monthly_transaction_date, category_id, sub_category_id, include_flg ) VALUES
( #{tran.userNo}, #{tran.monthlyTransactionName}, #{tran.monthlyTransactionAmount}, #{tran.monthlyTransactionDate}, #{tran.categoryId}, #{tran.subCategoryId}, TRUE )
;
