mapper.insert:
INSERT INTO
sub_category(
user_no,
category_id,
sub_category_name
)
VALUES(
#{userNo},
#{categoryId},
#{subCategoryName}
);

mapper.select:
SELECT
sub_category_id
FROM sub_category
WHERE user_no=#{userNo}
AND sub_category_name=#{subCategoryName};

mapper.insert:
INSERT INTO
hidden_sub_category(
user_no,
sub_category_id
)
VALUES(
#{userNo},
#{subCategoryId}
);
