mapper.insert:
INSERT INTO
user(
	user_id,
	email,
	password
)
VALUES(
	#{userId},
	#{email},
	#{password}
);

mapper.update:
UPDATE
	user
SET
	password=#{newPassword}
WHERE
	user_no=#{userNo}
AND
	password=#{password};

UPDATE
	user
SET
	email=#{email}
WHERE
	user_no=#{userNo}
AND
	password=#{password};

UPDATE
	user
SET
	theme_color_id=#{themeColorId}
WHERE
	user_no=#{userNo};
